package ppt3;

public class mathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(Math.min(3, 13)); // 3-13중에 가장 작은 수 
		System.out.println(Math.max(123, 54)); // 123-54중에 가장 큰 수 
		System.out.println(Math.abs(-23.1)); //절댓값 
		System.out.println(Math.sqrt(3)); // 제곱근 루트3
		System.out.println(Math.random()); // 랜덤
	}


	

}
